mapper.select:
select cities.id as city_id, cities.name as city_name,
countries.id as country_id, countries.name as country_name
from cities inner join countries on cities.fk_country_id = countries.id where cities.id = #{id}

mapper.update:
update cities set fk_country_id = #{countryId},
                  name = #{city.name}
where id = #{city.id}
