mapper.select:
select flights.id as flight_id,
airport_from.id as airport_from_id, airport_from.name as airport_from_name, airport_from.iata as airport_from_iata, airport_from.icao as airport_from_icao,
airport_to.id as airport_to_id, airport_to.name as airport_to_name, airport_to.iata as airport_to_iata_code, airport_to.icao as airport_to_icao_code,
airlines.id as airline_id, airlines.name as airline_name,
flights.price as flight_price, flights.departure_time as flight_departure_time, flights.arrival_time as flight_arrival_time
from flights inner join airports airport_from on flights.fk_airport_from_id = airport_from.id inner join airports airport_to on flights.fk_airport_to_id = airport_to.id inner join airlines on flights.fk_airline_id = airlines.id where airport_from.iata in
#{code}
and airport_to.iata in
#{code}
and jsonb_array_length(jsonb_path_query_array(free_seats, '$[*] ? (@.free == true)')) >= #{passengerAmount} and flights.departure_time between #{start} and #{end}

mapper.update:
update flights set fk_airline_id = #{airline.id},
                   departure_time = #{departureTime},
                   arrival_time = #{arrivalTime},
                   price = #{price}
where id = #{id}
